processOnlyListedSensors = msg.processOnlyListedSensors || false;

function calibrateHumidity2Points(humidity, offset1, offset2, calpoint1, calpoint2)
{
    p1y=calpoint1
	p2y=calpoint2
	p1x=p1y - offset1
	p2x=p2y - offset2
	m = (p1y - p2y) * 1.0 / (p1x - p2x)
	b = p2y - m * p2x //would be more efficient to do this calculations only once
	humidityCalibrated=m*humidity + b
	if (humidityCalibrated > 100 ) //with correct calibration this should not happen
	{
	    humidityCalibrated = 100
	}
	else if (humidityCalibrated < 0)
	{
		humidityCalibrated = 0
	}
	humidityCalibrated=Math.round(humidityCalibrated,0)
	return humidityCalibrated
}

//"A4:C1:38:69:E2:7C"
msg.topic = msg.payload.sensor;

if (msg.payload.sensor in msg.sensors )
{
    sensor =  msg.payload.sensor;
    sensordaten = msg.sensors[sensor];
    msg.payload.sensor = sensordaten.sensorname;
    msg.topic = msg.payload.sensor;
    if ("offset1" in sensordaten && "offset2" in sensordaten && "calpoint1" in sensordaten && "calpoint2" in sensordaten)
    {
        msg.payload.humidity = calibrateHumidity2Points(msg.payload.humidity, sensordaten.offset1, sensordaten.offset2, sensordaten.calpoint1,sensordaten.calpoint2);
    }
    else if ("humidityOffset" in sensordaten)
    {
        msg.payload.humidity = msg.payload.humidity + sensordaten.humidityOffset
    }

    return msg;
}
else if (! processOnlyListedSensors)
{
    return msg;
}
//drop message because only listed sensors should be processed here
